INSERT INTO pkm_conquest_move_displacements (conquest_move_displacement_id, name, name_alias, affects_target, short_effect, effect)
VALUES (1, 'Advance one tile', 'advance-1', 0, 'User advances one tile.', 'The user moves to the tile directly in front of it.  If the user cannot move to that tile or another Pokémon is already occupying that tile, the move cannot be used.'),
(2, 'Advance two tiles', 'advance-2', 0, 'User jumps ahead two tiles.', 'The user jumps ahead two tiles without crossing the tile in-between.  If the user cannot move to that tile or another Pokémon is already occupying that tile, the move cannot be used.'),
(3, 'Switch back', 'switch-back', 0, 'User retreats one tile, switching places with any Pokémon already there.', 'The user moves to the tile behind it, if it can move to that tile.  If another Pokémon is already occupying that tile, the user switches places with it.'),
(4, 'Knockback', 'knockback', 1, 'Deals knockback.', '$Target is knocked one tile backward in the direction the user is facing, if that tile is one it can move to and if no other Pokémon is already occupying that tile.'),
(5, 'Knockback & switch', 'knockback-switch', 1, 'Deals knockback, switching $target with the Pokémon behind it if applicable.', '$Target is knocked one tile backward in the direction the user is facing, if that tile is one it can move to.  If another Pokémon is already occupying that tile, the Pokémon switch places.');