INSERT INTO pkm_pokemon_move_methods (pokemon_move_method_id, name, name_alias, description)
VALUES (1, 'Level up', 'level-up', 'Learned when a Pokémon reaches a certain level.'),
(2, 'Egg', 'egg', 'Appears on a newly-hatched Pokémon, if the father had the same move.'),
(3, 'Tutor', 'tutor', 'Can be taught at any time by an NPC.'),
(4, 'Machine', 'machine', 'Can be taught at any time by using a TM or HM.'),
(5, 'Stadium: Surfing Pikachu', 'stadium-surfing-pikachu', 'Learned when a non-rental Pikachu helps beat Prime Cup Master Ball R-2.  It must participate in every battle, and you must win with no continues.'),
(6, 'Volt Tackle Pichu', 'light-ball-egg', 'Appears on a Pichu whose mother was holding a Light Ball.  The father cannot be Ditto.'),
(7, 'Colosseum: Purification', 'colosseum-purification', 'Appears on a Shadow Pokémon as it becomes increasingly purified.'),
(8, 'XD: Shadow', 'xd-shadow', 'Appears on a Snatched Shadow Pokémon.'),
(9, 'XD: Purification', 'xd-purification', 'Appears on a Shadow Pokémon as it becomes increasingly purified.'),
(10, 'Form Change', 'form-change', 'Appears when Rotom or Cosplay Pikachu changes form.  Disappears if the Pokémon becomes another form and this move can only be learned by form change.');